ActorFrame.OnCommand:
%function(self) InitScreen('ScreenGameplay');
self:queuecommand('KillBPM');
if GAMESTATE:GetEnv('Vibrate') == 'On' then
	self:vibrate();
	end
end

ActorFrame.KillBPMCommand:
%function(self)
SCREENMAN:GetTopScreen():GetChild('BPMDisplay'):addy(-10000);
			if gnZoomRatio > 1 then
				if GAMESTATE:IsHumanPlayer(PLAYER_1) and not GAMESTATE:PlayerUsingBothSides() then
					SCREENMAN:GetTopScreen():GetChild('PlayerP1'):addx(0-CourseHorizontal()/1.5);
					end
				if GAMESTATE:IsHumanPlayer(PLAYER_2) and not GAMESTATE:PlayerUsingBothSides() then
					SCREENMAN:GetTopScreen():GetChild('PlayerP2'):addx(0+CourseHorizontal()/1.5);
					end
				if GAMESTATE:IsDemonstration() then
					SCREENMAN:GetTopScreen():GetChild('PlayerP1'):addx(0+CourseHorizontal()/3.1);
					SCREENMAN:GetTopScreen():GetChild('PlayerP2'):addx(0-CourseHorizontal()/3.1);
					end
				end
end

layer.CheckerCommand:
%function(self)
			if MistAlpha >= 0.65 then
			self:linear(0.5);
			self:diffusealpha(1)
			else
			self:linear(0.5);
			self:diffusealpha(0);
			end
			self:queuecommand('Checker');
			end

%function(self)
			if MistAlpha >= 0.65 then
			self:linear(0.5);
			self:diffusealpha(1)
			else
			self:linear(0.5);
			self:diffusealpha(0);
			end
			self:queuecommand('Checker');
			end

Layer.StepP1MessageCommand:
%function(self)
				popTimer = popTimer + 1;
				if popTimer > 6 then
				popTimer = 1;
				end
				end

Layer.StepP2MessageCommand:
%function(self)
				popTimer2 = popTimer2 + 1;
				if popTimer2 > 6 then
				popTimer2 = 1;
				end
				end

ActorFrame.InitCommand:
%function(self)
						self:x(2000)
						self:y(SCREEN_CENTER_Y)
						popTimer = 0;
						popTimer2 = 0;
						P1Switch = 0;
						P2Switch = 0;
						P3Switch = 0;
						P4Switch = 0;
						P5Switch = 0;
						P6Switch = 0;

						end

ActorFrame.Update0Command:
%function(self)
			if popTimer == 1 then
				self:diffusealpha(1);
				self:zoom(0.1);
				self:x(-100+(100*math.random(3))+(10*math.random(9))+(1*math.random(9)));
				self:y(10+(100*math.random(2))+(10*math.random(7))+(1*math.random(9)));
				self:decelerate(0.25);
				self:zoom((math.random(2))+(0.1*math.random(9))+(0.01*math.random(9)));
				self:sleep(0.001);
				self:diffusealpha(0);
				else
				if popTimer == 6 then
					self:stoptweening();
					end
				end
			end

Layer.Update1Command:
%function(self)
				if popTimer == 1 then
					self:diffusealpha(1);
					self:rotationz(100*(math.random(-6,6)));
					self:decelerate(0.25);
					self:rotationz(0);
					self:diffusealpha(0);
				else
				if popTimer == 6 then
					self:stoptweening();
					end
				end
				end

Layer.Update2Command:
%function(self)
				if popTimer == 1 then	
					self:diffusealpha(1);
					self:rotationz(100*(math.random(-6,6)));
					self:decelerate(0.25);
					self:rotationz(0);
					self:diffusealpha(0);
				else
				if popTimer == 6 then
					self:stoptweening();
					end
				end
				end

Layer.Update3Command:
%function(self)
				if popTimer == 1 then
					if P1Switch == 0 then
					Rotit = (100*(math.random(-6,6)));
					Xit = (math.random(-2,2)*((10*math.random(-9,9))+(1*math.random(-9,9))));
					Yit = (-1*(10+(10*math.random(5,9))+(1*math.random(5,9))));

					self:diffusealpha(1);
					self:rotationz(Rotit);
					self:decelerate(0.2);
					self:addx(Xit);
					self:addy(Yit);
					self:rotationz(0);
					self:accelerate(0.2);

					Rotit = (Rotit * (-1));

					self:rotationz(Rotit);
					self:addx(Xit);
					self:addy(600);
					self:sleep(0.001);
					self:diffusealpha(0);
					P1Switch = 1;
					end
				else
				if popTimer == 6 then
					self:stoptweening();
					end
					P1Switch = 0;
					end
				end

