Layer.Update3Command:
%function(self)
				if popTimer2 == 3 then
					if P3Switch2 == 0 then
					Rotit = (100*(math.random(-6,6)));
					Xit = (math.random(-2,2)*((10*math.random(-9,9))+(1*math.random(-9,9))));
					Yit = (-1*(10+(10*math.random(5,9))+(1*math.random(5,9))));

					self:diffusealpha(1);
					self:rotationz(Rotit);
					self:decelerate(0.2);
					self:addx(Xit);
					self:addy(Yit);
					self:rotationz(0);
					self:accelerate(0.2);

					Rotit = (Rotit * (-1));

					self:rotationz(Rotit);
					self:addx(Xit);
					self:addy(600);
					self:sleep(0.001);
					self:diffusealpha(0);
					P3Switch2 = 1;
					end
				else
				if popTimer2 == 2 then
					self:stoptweening();
					end
