BitmapText.loopCommand:
%function(self)
			self:stoptweening();
			if curRate ~= 1 then self:settext(gnSongLength..' (x'..curRate..' Music Rate)'); else self:settext(gnSongLength); end
			self:sleep(0.03);
			self:queuecommand('loop');
			end

%function(self)
			self:stoptweening();
			if curRate ~= 1 then self:settext(gnSongLength..' (x'..curRate..' Music Rate)'); else self:settext(gnSongLength); end
			self:sleep(0.03);
			self:queuecommand('loop');
			end

Layer.StartCommand:
%function(self)
			self:diffusealpha(0);
			self:x(SCREEN_CENTER_X-162); self:y(SCREEN_CENTER_Y+143); self:zoom(0.1);self:draworder(5);
			self:queuecommand('loop');
			end

%function(self)
