ActorFrame.InitCommand:
%function(self) self:hibernate(0.02)
if gnRateRestore == nil then
	gnRateRestore = 0;
	end
if GAMESTATE:GetEnv('Wag') == 'On' then
	self:wag();
	end
gnGoBack = 0;

gnBlazedP1 = 0;
gnBlazedP2 = 0;
gnP1Ready = 0;
gnP2Ready = 0;

gnTotalDone = 0;

gnSpecialUSB = false;
if gnStatFrequency == nil then gnStatFrequency = 0; end
gnStatFrequency = gnStatFrequency + 1;
end
