Layer.InitCommand:
%function(self)
			self:diffusealpha(0);
			if GAMESTATE:IsHumanPlayer(PLAYER_1) then
				self:x(245+AddFromCenter('x','Failed',PLAYER_1));
				else
				self:x(-64+AddFromCenter('x','Failed',PLAYER_2));
				end
			if GAMESTATE:IsHumanPlayer(PLAYER_1) then self:y(40+AddFromCenter('y','Failed',PLAYER_1)); else  self:y(40+AddFromCenter('y','Failed',PLAYER_2)); end
			self:queuecommand('ColourIt');
			end

Layer.DurationCommand:
%function(self)
				if gnDimBackgroundSeconds == nil then
					gnDimBackgroundSeconds = 0.1;
					end
				if gnDimBackgroundSeconds == 0 then
					gnDimBackgroundSeconds = 0.1;
					end
			self:sleep(gnDimBackgroundSeconds);
			self:linear(1);
			self:diffusealpha(0);
			end

Layer.InitCommand:
%function(self)
		if GAMESTATE:IsHumanPlayer(PLAYER_1) then
			self:x(244+AddFromCenter('x','Failed',PLAYER_1));
			else
			self:x(-66+AddFromCenter('x','Failed',PLAYER_2));
			end
		if GAMESTATE:IsHumanPlayer(PLAYER_1) then self:y(40+AddFromCenter('y','Failed',PLAYER_1)); else  self:y(40+AddFromCenter('y','Failed',PLAYER_2)); end
		self:diffusealpha(0);
		self:zoom(2);
		self:decelerate(0.5);
		self:diffusealpha(gnDiffusealpha);
		self:zoom(gnZoom);
		gnPath = getCustomFailedSound();
		SOUND:PlayOnce(gnPath);
				if gnOnScreenSeconds == nil then
					gnOnScreenSeconds = 0.1;
					end
				if gnOnScreenSeconds == 0 then
					gnOnScreenSeconds = 0.1;
					end
		self:sleep(gnOnScreenSeconds);
		self:linear(1);
		self:diffusealpha(0)
		end;
